<?php	class Review extends CI_Controller {	private $_data = array();	function __construct()	{		parent::__construct();		if($this->session->userdata('adminId') == ''){			redirect($this->config->item('base_url'));        }		ini_set("allow_url_fopen",1);		$this->load->model('review_model');	}	function lists()	{		$this->load->library('pagination');		$url_to_paging = $this->config->item('base_url');		$config['base_url'] = $url_to_paging.'review/lists/';		$config['per_page'] = '10000';		$config['first_url']='0';		$data = array();		//using for searching data...		$data['categoryname'] = $this->input->post('categoryname');		$per_page = '1';		$perpage = '3';		//below is used in all perpose		$return = $this->review_model->lists($config['per_page'],$this->uri->segment(3), $data);		$data['result'] = $return['result'];		$config['total_rows'] = $return['count'];		//echo "<pre>";print_r($data);break;		$this->pagination->initialize($config);		$this->load->view('list_review', $data);	}	function updatestatus($id,$value)	{			$result=$this->review_model->updatestatus($id,$value);		$this->session->set_flashdata('L_strErrorMessage','Status Updated Succcessfully!!!!');		redirect($this->config->item('base_url').'review/lists');		//$this->load->view('users/list_user', $data);	}        function deletes()        {            if(isset($_POST['selected']) && count($_POST['selected']) > 0) {                foreach($_POST['selected'] as $selCheck) {                    if($this->review_model->deletes($selCheck)) {                        $this->session->set_flashdata('L_strErrorMessage','Review Deleted Successfully!!!!');                    }                    else                    {                        $this->session->set_flashdata('flashError','Some Errors prevented from Deleting!!!!');                    }                }            }            redirect($this->config->item('base_url').'review/lists');        }    }?>