<?php	class Detail_form extends CI_Controller {	private $_data = array();	function __construct()	{			private $data = array();		parent::__construct();		if($this->session->userdata('adminId') == ''){			redirect($this->config->item('base_url'));        }		// ini_set("allow_url_fopen",1);		 parse_str($_SERVER['QUERY_STRING'], $_GET);		$this->load->model('detail_form_model');	}	function lists()	{			echo "test";exit;		$this->load->library('pagination');		$url_to_paging = $this->config->item('base_url');		$config['base_url'] = $url_to_paging.'detail_form/lists/';		$config['per_page'] = '10000';		$config['first_url']='0';		$data = array();		//using for searching data...		$data['categoryname'] = $this->input->post('categoryname');		$per_page = '1';		$perpage = '3';		//below is used in all perpose		$return = $this->detail_form_model->lists($config['per_page'],$this->uri->segment(3), $data);		$data['result'] = $return['result'];		$config['total_rows'] = $return['count'];		//echo "<pre>";print_r($data);break;		$this->pagination->initialize($config);		$this->load->view('list_detail_form', $data);	}	// function updatestatus($id,$value)	// {		// 	$result=$this->detail_form_model->updatestatus($id,$value);	// 	$this->session->set_flashdata('L_strErrorMessage','Status Updated Succcessfully!!!!');	// 	redirect($this->config->item('base_url').'detail_form/lists');	// 	//$this->load->view('users/list_user', $data);	// }        function deletes()        {            if(isset($_POST['selected']) && count($_POST['selected']) > 0) {                foreach($_POST['selected'] as $selCheck) {                    if($this->detail_form_model->deletes($selCheck)) {                        $this->session->set_flashdata('L_strErrorMessage','Detail Form Deleted Successfully!!!!');                    }                    else                    {                        $this->session->set_flashdata('flashError','Some Errors prevented from Deleting!!!!');                    }                }            }            redirect($this->config->item('base_url').'detail_form/lists');        }    }?>