<?php	class access_risk_model extends CI_Model {	private $_data = array();	function __construct() {		parent::__construct();	}	  function get_user($id){		  $this->db->where('id = ',$id);		  $query = $this->db->get('detail_form');			  if ($query->num_rows() > 0)			  {			  	$result = $query->result();			  	return $result;			  } else {				  return false;			  }		}	  function edit_pass($content) {		  $data['password']  = $content['newpassword'];	 	  $this->_data = $data;		  $this->db->where('id', $this->session->userdata('adminId'));		  if ($this->db->update('admin_user', $this->_data))		  {					  	return true;		  } else {				  	return false;		  }	} function lists($num, $offset, $content) 	{		if($offset == '')		{			$offset = '0';		}		$sql = "SELECT * FROM detail_form where id <> 0";		if($num!='' || $offset!='')		{			$sql .=	"  order by id desc limit ".$offset." , ".$num." ";		}		$query = $this->db->query($sql);		$sql_count = "SELECT * FROM  detail_form  WHERE id <> 0";		$query1 = $this->db->query($sql_count);		$ret['result'] = $query->result();		$ret['count']  = $query1->num_rows();	    return $ret;	}	function deletes($id) 	{ 		$this->db->where('id = ',$id);		if ($this->db->delete('detail_form'))	{			return true;		} else {			return false;		}	}	function get_industry_name($id)	{			$sql = "SELECT * from industry where id='".$id."' ";		$query = $this->db->query($sql);		if ($query->num_rows() > 0)		{			$result = $query->row()->name;			return $result;		}	}	function get_sub_industry_name($id)	{			$sql = "SELECT * from sub_industry where id='".$id."' ";		$query = $this->db->query($sql);		if ($query->num_rows() > 0)		{			$result = $query->row()->name;			return $result;		}	}	function get_category($id)	{		$this->db->where('id',$id);		$query = $this->db->get('detail_form');		if($query->num_rows() > 0)		{			return $query->row();		}		else		{			return false;		}	}	function get_productname($id)	{		$this->db->where('id',$id);		$query = $this->db->get('product');		if($query->num_rows() > 0)		{			return $query->row()->name;		}		else		{			return false;		}	}	function is_already_exist_add($user)	{		$this->db->where('email',$user['email']);		$query = $this->db->get('buy_now');		if($query->num_rows() > 0)		{			return true;		}		else		{			return false;		}	}		function is_already_exist_edit($id,$user)	{		$this->db->where('id !=',$id);		$this->db->where('email_id',$user['email_id']);		$query = $this->db->get('customer');		if($query->num_rows() > 0)		{			return true;		}		else		{			return false;		}	}	function updatestatus($id,$is_active){		$sql= " update brochure set is_active= '".$is_active."' where id='".$id."' ";	if ($query = $this->db->query($sql)){		return true;			} else {		return false;		}	}	}?>