<?php	class Detail_form_model extends CI_Model {	private $_data = array();	function __construct() {		parent::__construct();	}    function lists($num, $offset, $content) 	{		if($offset == '')		{			$offset = '0';		}		$sql = "SELECT * FROM detail_form where id <> 0 ";		if($content['categoryname'] != '') 		{			$sql .=	" AND  (name like '%".$content['categoryname']."%' ) "; 		}		if($num!='' || $offset!='')		{			$sql .=	"  order by id desc limit ".$offset." , ".$num." ";		}		$query = $this->db->query($sql);		$sql_count = "SELECT * FROM  detail_form  WHERE id <> 0";		if($content['categoryname'] !='')		{			$sql_count .= " AND `name` like '".$content['categoryname']."%'";		}		$query1 = $this->db->query($sql_count);		$ret['result'] = $query->result();		$ret['count']  = $query1->num_rows();	    return $ret;	}    function deletes($id)    {        $this->db->where('id = ',$id);        if ($this->db->delete('detail_form'))	{            return true;        } else {            return false;        }    }	function get_industry_name($id)	{			$sql = "SELECT * from industry where id='".$id."' ";		$query = $this->db->query($sql);		if ($query->num_rows() > 0)		{			$result = $query->row()->name;			return $result;		}	}	function get_sub_industry_name($id)	{			$sql = "SELECT * from sub_industry where id='".$id."' ";		$query = $this->db->query($sql);		if ($query->num_rows() > 0)		{			$result = $query->row()->name;			return $result;		}	}	function updatestatus($id,$is_active)	{			$sql= "update detail_form set status= '".$is_active."' where id='".$id."' ";				if ($query = $this->db->query($sql))				{							return true;					} else {							return false;						}		}}?>